import { POST_DATA_SUCCESS,FETCH_POST_ERROR,GET_DATA_SUCCESS } from "../actions/type"

const initialState = {
    requesting: false,
    success: false,
    message: null,
    data: null
}
function PostReducers(state = initialState, payload) {
    switch (payload.type) {
        case POST_DATA_SUCCESS:
            return {
                ...state,
                requesting: true
            };
        case GET_DATA_SUCCESS:
            return {
                ...state,
                requesting: false,
                success: true,
                data: payload.data
            };
        case FETCH_POST_ERROR:
            return {
                ...state,
                requesting: false,
                message: payload.message
            };

        default:
            return state;
    }
}

export default PostReducers;
