import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { loadPosts } from "../redux/actions/postAction";
import Filter from "./filter-component";
import ProjectItem from "./project-item";
import ButtonComponent from "./button-component";
impor

export default function Body() {
  const requesting = useSelector((state) => state.posts.requesting);
  const data = useSelector((state) => state.posts.data);
  const total_page=useSelector((state)=> state.posts.total_page);
  const dispatch = useDispatch();
console.log(total_page);
  useEffect(() => {
    dispatch(loadPosts(0));
    console.log();
  }, [dispatch]);

  return (
    <div className="l-content">
      <div className="container">
        <Filter />
        <div className="c-box">
          <div className="c-box__title">
            <h2 className="c-box__title__name">
              <span className="is-small">Dự án</span>
              <br />
              <b>Nhiều người chơi</b>
            </h2>
          </div>
          <div className="c-box__content">
            <div className="c-project-grid">
              <ul>
                {requesting ? (
                  <div >Loading...</div>
                ) : data && data.length > 0 ? (
                  data.map((item) => (
                    <ProjectItem
                      thumbnail={item.thumbnail}
                      name={item.name}
                      full_name={item.created_by.full_name}
                      avatar={item.created_by.avatar}
                    />
                  ))
                ) : (
                  <div>Data is empty</div>
                )}
              </ul>
            </div>
            <div className="b-page">
              <ul className="pagination justify-content-center">
                <li className="page-item disabled">
                  <span className="page-link">
                    <i className="fa fa-angle-left" />
                  </span>
                </li>
                <li className="page-item ">
                  <button
                    className="page-link"
                    onClick={() => {
                      dispatch(loadPosts(1));
                    }}
                  >
                    1
                  </button>
                </li>
                <li className="page-item ">
                  <ButtonComponent
                    className="page-link"
                    onClick={() => {
                      dispatch(loadPosts(2));
                    }}
                  >
                    2
                  </ButtonComponent>
                </li>
                <li className="page-item">
                  <button
                    className="page-link"
                    onClick={() => {
                      dispatch(loadPosts(3));
                    }}
                  >
                    3
                  </button>
                </li>
                <li className="page-item">
                  <a className="page-link" href="#">
                    <i className="fa fa-angle-right" />
                  </a>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
