import { FETCH_POST_REQUEST,FETCH_POST_ERROR,FETCH_POST_SUCCESS } from "../constants/post"

const initialState = {
    requesting: false,
    success: false,
    message: null,
    data: null
}
function Reducers(state = initialState, payload) {
    switch (payload.type) {
        case FETCH_POST_REQUEST:
            return {
                ...state,
                requesting: true
            };
        case FETCH__SUCCESS:
            return {
                ...state,
                requesting: false,
                success: true,
                data: payload.data
            };
        case FETCH__ERROR:
            return {
                ...state,
                requesting: false,
                message: payload.message
            };

        default:
            return state;
    }
}

export default Reducers;
