import { FETCH_POST_ERROR, FETCH_POST_REQUEST, FETCH_POST_SUCCESS } from "../constants/post";
import axios from "axios";

export const loadPosts = () => async dispatch => {
    try {
        dispatch({ type: FETCH_POST_REQUEST });
        const res = await axios.get('https://dev.api.codekitten.vn/api/v1/project/search?page=1') 
        const responseBody = res.data.data.data;
        console.log( responseBody)
        dispatch({
            type: FETCH_POST_SUCCESS,
            data: responseBody
        });
    } catch (error) {
        console.error(error);
        dispatch({
            type: FETCH_POST_ERROR,
            message: error
        });
    }
}

