import { FETCH_POST_REQUEST,FETCH_POST_ERROR,FETCH_POST_SUCCESS } from "../constants/post"
import data from '.../'
const initialState = {
    requesting: false,
    success: false,
    message: null,
    data: null
}
function PostReducers(state = initialState, payload) {
    switch (payload.type) {
        case FETCH_POST_REQUEST:
            return {
                ...state,
                requesting: true
            };
        case FETCH_POST_SUCCESS:
            return {
                ...state,
                requesting: false,
                success: true,
                data: payload.data
            };
        case FETCH_POST_ERROR:
            return {
                ...state,
                requesting: false,
                message: payload.message
            };

        default:
            return state;
    }
}

export default PostReducers;
