import axios from "axios";
import {
  FETCH_POST_REQUEST,
  FETCH_POST_SUCCESS,
  FETCH_POST_ERROR,
} from "../actions/type";

export const loadPosts = (page,name) => async (dispatch) => {
  try {
    dispatch({ type: FETCH_POST_REQUEST });

    const res = await axios.get(
      `https://dev.api.codekitten.vn/api/v1/project/search?name=%20&page=${page}`
    );
    const responseBody = res.data.data.data;
    const total_records = res.data.data.total_records;
    const total_page = res.data.data.total_page;
    const current_page = res.data.data.current_page;
    console.log(responseBody);
    console.log(res.data.data);
    console.log(total_records);
    dispatch({
      type: FETCH_POST_SUCCESS,
      data: responseBody,
      total_page: total_page,
      total_records: total_records,
      current_page: current_page
    });
  } catch (error) {
    console.error(error);
    dispatch({
      type: FETCH_POST_ERROR,
      message: error,
    });
  }
};
