import { useDispatch, useSelector } from "react-redux";
import { loadPosts } from "../redux/actions/postAction";
import React from "react";
import ReactPaginate from "react-paginate";
import SweetPagination from "sweetpagination";

export default function Pagination1() {
  const nameSearch = useSelector((state) => state.posts.nameSearch);
  const dispatch = useDispatch();
  const total_page = useSelector((state) => state.posts.total_page);
  const current_page = useSelector((state) => state.posts.current_page);
  const total_records = useSelector((state) => state.posts.total_records);


  const handleClick = (e) => {
    dispatch(loadPosts(e.selected, nameSearch));
  };

  return (
    <>
      <ul className="pagination justify-content-center">
        <li className={"page-item "}>
          <ReactPaginate
            onPageChange={handleClick}
            pageRangeDisplayed={2}
            marginPagesDisplayed={2}
            pageCount={total_page}
            breakLabel="..."
            containerClassName="page-item"
            activeClassName="active"
            renderOnZeroPageCount={null}
          />
         <SweetPagination
        currentPageData={current_page}
        dataPerPage={12}
        getData={total_records}
        navigation={true}
      />
        </li>
      </ul>
    </>
  );
}
