import { useDispatch, useSelector } from "react-redux";
import { loadPosts } from "../redux/actions/postAction";
import React from "react";
import PropTypes from "prop-types";
import ReactPagination from 'react-paginate';

function Pagination(props) {
  const total_page = useSelector((state) => state.posts.total_page);

  const current_page = useSelector((state) => state.posts.current_page);
  const dispatch = useDispatch();
 
  return (
    <>
    {/*  <li className={`page-item ${current_page === props.num ? " active" : ""}`}>
      <button
        className="page-link"
        onClick={() => {
          handleClick(dispatch(loadPosts(props.num, props.name)));
        }}
      >
        {props.num}
      </button> 
      </li>
      */}
      <ul className="pagination justify-content-center">
      <ReactPagination 
      pageCount={total_page}
      breakLabel={"..."}
      onPageChange={dispatch(loadPosts(props.num, props.name))}
      containerClassName={"page-item"}
      activeClassName={"active"}
       />
       
      </ul>

    </>
  );
}

Pagination.propTypes = {
  num: PropTypes.number,
  page: PropTypes.number,
  name: PropTypes.string,
};

export default Pagination;
