import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { loadPosts } from "./redux/actions/postAction";
import { useEffect } from "react";

export default function ProjectItem() {
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(loadPosts());
  }, [dispatch]);
  const data = useSelector((state) => state.posts.data);
  const requesting = useSelector((state) => state.posts.requesting);

  return requesting ? (
    <img src="./static/images/logo.png" className="App-logo" alt="logo" />
  ) : data && data.length > 0 ? (
    <div class="c-project-item">
    
      <div class="c-project-item__thumb">
        <a href="project-detail.html">
          <img src={props.project} alt="project" />
        </a>
      </div>
      <div class="c-project-item__content">
        <div class="c-project-item__avatar">
          <img src={props.avatar} alt="avatar" />
        </div>
        <div class="c-project-item__row">
          <h3 class="c-project-item__title">
            <a href="project-detail.html">{props.title}</a>
          </h3>
          <span class="c-project-item__author">
            Bởi:
            <b> {props.name}</b>
          </span>
        </div>
        <div class="c-project-item__row">
          <a class="c-project-item__link" href="project-detail.html">
            Xem hướng dẫn
          </a>
          <a class="c-project-item__link" href="#">
            Chơi ngay
          </a>
        </div>
      </div>
    </div>
  ) : (
    <div>Data is empty</div>
  );
}
